package ch02.sec01;

public class VariableInitializationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int value = 0; //변수 value 선언 +변수 초기화 설정
		int result = value+10; //연산 결과를 변수 result 의 초기값으로 대입
		
		System.out.println(result); //변수 result 값을 읽고 콘솔에 츨력

	}

}
